<div class="row">
	<form>
		<div class="input-field col s6 m4">
			<select>
				<option value="aluga">Aluga</option>
				<option value="vende">Vende</option>
			</select>
			<label>Status</label>
		</div>
		<div class="input-field col s6 m4">
			<select>
				<option value="1">Porta</option>
				<option value="2">Janela</option>
			</select>
			<label>Tipo</label>
		</div>
		<div class="input-field col s6 m4">
